// product.service.ts

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  getProducts() {
    // Simulate fetching product data from a server try to implement an API and use the columns like below 
    return [
      { id: 1, name: 'Natural Turmeric', price: 0.1, imageUrl: 'assets/images/turmeric.jpeg' },
      { id: 2, name: 'Cashews', price: 20, imageUrl: 'assets/images/p1.jpeg' },
      { id: 3, name: 'Natural Red Chilli', price: 20, imageUrl: 'assets/images/redchilli.jpeg' },
      { id: 2, name: 'Walnuts', price: 20, imageUrl: 'assets/images/walnuts.jpeg' },
      { id: 2, name: 'Wild Raw Honey', price: 20, imageUrl: 'assets/images/honey.jpeg' },
      { id: 2, name: 'Kashmiri Saffron', price: 20, imageUrl: 'assets/images/saffron.jpeg' },
      { id: 2, name: 'Desi cow ghee', price: 20, imageUrl: 'assets/images/cowghee.jpeg' },
      { id: 2, name: 'Natural Saffron', price: 20, imageUrl: 'assets/images/saffron.jpeg' },
    ];
  }
}
