// product.service.ts

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  getProducts() {
    // Simulate fetching product data from a server try to implement an API and use the columns like below 
    return [
      { id: 1, name: 'Natural Turmeric', price: 0.1, imageUrl: 'assets/images/turmeric.jpg' },
      { id: 2, name: 'Cashews', price: 20, imageUrl: 'assets/images/p1.jpg' },
    ];
  }
}
